mapper.select:
SELECT * FROM JOIN_ASK LEFT OUTER JOIN FILE ON JOIN_ASK.FILE_ID = FILE.FILE_ID WHERE JOIN_ASK.DEL_YN = 'N'
AND JOIN_ASK.COMPANY_NM LIKE CONCAT("%",#{keyword},"%")
ORDER BY JOIN_ASK.REG_DATE DESC LIMIT #{i}, #{contentnum}

mapper.update:
		UPDATE
		JOIN_ASK
		SET INFO = #{info}
    	WHERE IDX = #{idx}

mapper.select:
SELECT * FROM PRODUCT_DETAIL
WHERE PRODUCT_IDX = #{productIdx}
AND THICKNESS = #{thickness}
AND SIZE = #{size}

mapper.update:
UPDATE WAREHOUSE
SET FILE_ID = #{fileId}, WAREHOUSE = #{warehouse}
WHERE WAREHOUSE_IDX = #{warehouseIdx}

mapper.select:
SELECT COUNT(*) FROM DEPOSIT_HISTORY
WHERE COMPANY_NM LIKE CONCAT("%",#{keyword},"%")
AND DATE(DEP_DATE) BETWEEN #{startDt} AND #{endDt}
WHERE DATE(DEP_DATE) BETWEEN #{startDt} AND #{endDt}

SELECT T.SEQ,T.DEP_DATE,T.ID, DEP, T.COMPANY_NM, T.CREDIT_AMOUNT,T.IDX, SUM(T.DEP) OVER(PARTITION BY T.ID ORDER BY T.SEQ) AS TOTAL_DEP, SUM(T.OD) OVER(PARTITION BY T.ID ORDER BY T.SEQ) AS TOTAL_ORDER FROM( SELECT ROW_NUMBER() OVER() AS SEQ, member.CREDIT_AMOUNT, member.COMPANY_NM , member.ID, HISTORY.DEP_MOUNT AS DEP, HISTORY.DEP_DATE, HISTORY.IDX, HISTORY.ORDER_MOUNT AS OD FROM DEPOSIT_HISTORY HISTORY INNER JOIN MEMBER MEMBER ON MEMBER.ID = HISTORY.MEMBER_ID
WHERE MEMBER.COMPANY_NM LIKE CONCAT("%",#{keyword},"%")
AND DATE(DEP_DATE) BETWEEN #{startDt} AND #{endDt}
WHERE DATE(DEP_DATE) BETWEEN #{startDt} AND #{endDt}
GROUP BY HISTORY.IDX,MEMBER.ID ORDER BY HISTORY.DEP_DATE DESC ) T ORDER BY T.DEP_DATE DESC LIMIT #{i}, #{contentnum}

SELECT
COUNT(*)
FROM
WAREHOUSE_INVENTORY A
INNER JOIN PRODUCT_THICKNESS B ON A.THICKNESS = B.THICKNESS_IDX
INNER JOIN PRODUCT C ON C.PRODUCT_IDX = A.PRODUCT_IDX
INNER JOIN PRODUCT_SIZE D ON D.SIZE_IDX = A.SIZE
INNER JOIN WAREHOUSE E ON E.WAREHOUSE_IDX = A.WAREHOUSE_IDX

WHERE


C.PRODUCT_IDX= #{productIdx}


E.WAREHOUSE_IDX = #{warehouseIdx}


AND E.WAREHOUSE_IDX = #{warehouseIdx}


AND  D.SIZE_IDX = #{size}


AND  B.THICKNESS_IDX = #{thickness}

SELECT
A.REG_DATE, A.REASON, A.STOCK, C.PRODUCT_CD,B.THICKNESS,C.PRODUCT_NM,D.HEIGHT,D.WIDTH,D.SIZE,E.WAREHOUSE
FROM
WAREHOUSE_INVENTORY A
INNER JOIN PRODUCT_THICKNESS B ON A.THICKNESS = B.THICKNESS_IDX
INNER JOIN PRODUCT C ON C.PRODUCT_IDX = A.PRODUCT_IDX
INNER JOIN PRODUCT_SIZE D ON D.SIZE_IDX = A.SIZE
INNER JOIN WAREHOUSE E ON E.WAREHOUSE_IDX = A.WAREHOUSE_IDX

WHERE


C.PRODUCT_IDX = #{productIdx}


E.WAREHOUSE_IDX = #{warehouseIdx}


AND E.WAREHOUSE_IDX = #{warehouseIdx}


AND  D.SIZE_IDX = #{size}


AND  B.THICKNESS_IDX = #{thickness}

ORDER BY A.REG_DATE DESC
LIMIT #{i}, #{contentnum}

SELECT * FROM MEMBER WHERE ROLE = '1'
AND COMPANY_NM LIKE CONCAT("%",#{keyword},"%")
LIMIT #{i}, #{contentnum}

