mapper.select:
SELECT COUNT(*) FROM MEMBER WHERE DEL_YN = 'N'
AND ID = #{keyword}
AND COMPANY_NM = #{keyword}

SELECT PRODUCT.PRODUCT_NM, SIZE.SIZE, SIZE.HEIGHT, SIZE.WIDTH, THICKNESS.THICKNESS, PRODUCT.PRODUCT_CD FROM PRODUCT_SIZE SIZE LEFT OUTER JOIN PRODUCT_THICKNESS THICKNESS ON SIZE.PRODUCT_NM = THICKNESS.PRODUCT_NM LEFT OUTER JOIN PRODUCT PRODUCT ON PRODUCT.PRODUCT_NM = THICKNESS.PRODUCT_NM WHERE PRODUCT.DEL_YN = 'N'
AND PRODUCT.PRODUCT_NM = #{keyword}
LIMIT #{i}, #{contentnum}

mapper.insert:
    INSERT INTO CLIENT_MONEY_INFO
	(
	company_nm
	)
	VALUES
	(
	#{companyNm}	
	)
	ON DUPLICATE KEY
	UPDATE
    company_nm = #{companyNm}
